var AceEditor, ContextMenu, FormSampleTemplate, FuzzyListView, Jadelet, Login, MenuBar, Modal, Observable, Progress, Style, Table, Window, addWindow, applyStyle, contextMenu, desktop, element, notepadMenuParsed, notepadMenuText, o, parseMenu, sampleMenuParsed, _ref, _ref1;

_ref = require("./lib/ui/index"), AceEditor = _ref.AceEditor, applyStyle = _ref.applyStyle, ContextMenu = _ref.ContextMenu, FuzzyListView = _ref.FuzzyListView, Login = _ref.Login, MenuBar = _ref.MenuBar, Modal = _ref.Modal, Observable = _ref.Observable, (_ref1 = _ref.Util, parseMenu = _ref1.parseMenu), Progress = _ref.Progress, Style = _ref.Style, Table = _ref.Table, Window = _ref.Window;

o = require("./util").o;

Jadelet = require("./lib/jadelet");

notepadMenuText = require("./samples/notepad-menu");

notepadMenuParsed = parseMenu(notepadMenuText);

FormSampleTemplate = require("./samples/test-form");

global.PACKAGE = PACKAGE;

applyStyle(Style.all, "system");

applyStyle(require("./style/demo"), "demo");

sampleMenuParsed = parseMenu("[M]odal\n  [A]lert\n  [C]onfirm\n  [P]rompt\n  [F]orm\n  F[u]zzy List\n  P[r]ogress\n[T]est Nesting\n  Test[1]\n    Hello\n    Wat\n  Test[2]\n    [N]ested\n    -----\n    [R]ad\n      So Rad\n      =====\n      Hella\n        Hecka\n          Super Hecka\n            Wicked\n        ---\n        -\n        -\n        ==\n[W]indow\n  [L]ogin\n  New [I]mage -> newImage\n  New [P]ixel -> newPixel\n  New [T]ext -> newText\n  New [S]preadsheet -> newSheet");

element = MenuBar({
  items: sampleMenuParsed,
  handlers: {
    alert: function() {
      return Modal.alert("yolo");
    },
    prompt: function() {
      return Modal.prompt("Pretty cool, eh?", "Yeah!").then(console.log);
    },
    confirm: function() {
      return Modal.confirm("Jawsome!").then(console.log);
    },
    form: function() {
      return Modal.form(FormSampleTemplate({
        cancel: function(e) {
          e.preventDefault();
          return Modal.hide();
        }
      })).then(console.log);
    },
    fuzzyList: function() {
      var view;
      view = FuzzyListView({
        items: function() {
          return Object.keys(PACKAGE.source);
        },
        ItemTemplate: function(item) {
          var Template, content;
          content = PACKAGE.source[item].content;
          Template = Jadelet.exec("div\n  span.name @name\n  span.length(style=\"float: right; font-style: italic; margin-right: 1rem\") @size");
          return Template({
            name: item,
            size: content.length
          });
        },
        maxItems: function() {
          return 9999;
        },
        submit: function(item) {
          Modal.hide();
          return console.log(PACKAGE.source[item]);
        }
      });
      return Modal.form(view.element, {
        cancellable: true
      });
    },
    login: function() {
      return new Promise(function(resolve, reject) {
        return Login({
          resolve: resolve,
          reject: reject
        });
      }).then(function(account) {
        return console.log(account);
      })["catch"](function() {
        return console.log("Login cancelled");
      });
    },
    progress: function() {
      var initialMessage, intervalId, progressView;
      initialMessage = "Reticulating splines";
      progressView = Progress({
        value: 0,
        max: 2,
        message: initialMessage
      });
      Modal.show(progressView.element, {
        cancellable: false
      });
      return intervalId = setInterval(function() {
        var ellipses, ellipsesCount, newValue, _i, _results;
        newValue = progressView.value() + 1 / 60;
        ellipsesCount = Math.floor(newValue * 4) % 4;
        ellipses = (function() {
          _results = [];
          for (var _i = 0; 0 <= ellipsesCount ? _i < ellipsesCount : _i > ellipsesCount; 0 <= ellipsesCount ? _i++ : _i--){ _results.push(_i); }
          return _results;
        }).apply(this).map(function() {
          return ".";
        }).join("");
        progressView.value(newValue);
        progressView.message(initialMessage + ellipses);
        if (newValue > 2) {
          clearInterval(intervalId);
          return Modal.hide();
        }
      }, 15);
    },
    newImage: function() {
      var img;
      img = document.createElement("img");
      img.src = "https://s3.amazonaws.com/whimsyspace-databucket-1g3p6d9lcl6x1/danielx/data/pI1mvEvxcXJk4mNHNUW-kZsNJsrPDXcAtgguyYETRXQ";
      return addWindow({
        title: "Yoo",
        content: img,
        iconEmoji: "üíº"
      });
    },
    newPixel: function() {
      var frame;
      frame = document.createElement("iframe");
      frame.src = "https://danielx.net/pixel-editor/embedded/";
      return addWindow({
        title: "Pixel",
        content: frame
      });
    },
    newText: function() {
      return AceEditor.preload().then(function() {
        var editor;
        editor = AceEditor();
        return addWindow({
          title: "Notepad.exe",
          content: editor.element
        });
      });
    },
    newSheet: function() {
      var InputTemplate, RowElement, data, menuBar, tableView;
      data = [0, 1, 2, 3, 4].map(function(i) {
        return {
          id: i,
          name: "yolo",
          color: "#FF0000"
        };
      });
      InputTemplate = require("./templates/input");
      RowElement = function(datum) {
        var tr, types;
        tr = document.createElement("tr");
        types = ["number", "text", "color"];
        Object.keys(datum).forEach(function(key, i) {
          var td;
          td = document.createElement("td");
          td.appendChild(InputTemplate({
            value: o(datum, key),
            type: types[i]
          }));
          return tr.appendChild(td);
        });
        return tr;
      };
      element = (tableView = Table({
        data: data,
        RowElement: RowElement
      })).element;
      menuBar = MenuBar({
        items: parseMenu("Insert\n  Row -> insertRow\nHelp\n  About"),
        handlers: {
          about: function() {
            return Modal.alert("Spreadsheet v0.0.1 by Daniel X Moore");
          },
          insertRow: function() {
            data.push({
              id: 50,
              name: "new",
              color: "#FF00FF"
            });
            return tableView.render();
          }
        }
      });
      return addWindow({
        title: "Spreadsheet [DEMO VERSION]",
        content: element,
        menuBar: menuBar.element
      });
    }
  }
}).element;

document.body.appendChild(element);

desktop = document.createElement("desktop");

document.body.appendChild(desktop);

contextMenu = ContextMenu({
  items: sampleMenuParsed[1][1],
  handlers: {}
