var Bindable, MountFS, PkgFS, testPkg, testPkg2;

require("/setup");

Bindable = require("/lib/bindable");

MountFS = require("/lib/fs/mount");

PkgFS = require("/lib/fs/pkg");

testPkg = {
  source: {
    "hello.coffee": {
      content: "alert hello"
    },
    "test/wat.js": {
      content: "assert(wat);"
    }
  }
};

testPkg2 = {
  source: {
    "yo.coffee": {
      content: "alert 'ayyy'"
    }
  }
};

describe("Mount FS", function() {
  it("should mount a filesystem", function() {
    var fs;
    fs = MountFS();
    fs.mount("/pkg/", PkgFS(testPkg));
    return fs.read("/pkg/hello.coffee").then(function(blob) {
      return blob.text();
    }).then(function(src) {
      return assert.equal(src, "alert hello");
    });
  });
  it("should mount on top of previous path", function() {
    var fs;
    fs = MountFS();
    fs.mount("/pkg/", PkgFS(testPkg));
    fs.mount("/pkg/", PkgFS(testPkg2));
    return fs.read("/pkg/yo.coffee").then(function(blob) {
      return blob.text();
    }).then(function(src) {
      assert.equal(src, "alert 'ayyy'");
      return fs.list('/pkg/').then(function(files) {
        assert.equal(files.length, 1);
        assert.equal(files[0].path, "/pkg/yo.coffee");
        fs.mount("/pkg/", PkgFS(testPkg));
        fs.mount("/pkg/", PkgFS(testPkg));
        return fs.list('/pkg/').then(function(files) {
          assert.equal(files.length, 2);
          return assert.equal(files[1].path, "/pkg/hello.coffee");
        });
      });
    });
  });
  it("should accept text strings in write method and save them with the proper media type passing options through", function() {
    var file, fs, opt;
    fs = MountFS();
    file = void 0;
    opt = void 0;
    fs.mount("/pkg/", {
      write: function(path, blob, options) {
        file = blob;
        opt = options;
        return Promise.resolve();
      },
      read: function(path) {
        return Promise.resolve(file);
      },
      on: function() {}
    });
    return fs.write("/pkg/hey.js", "alert('hey');", {
      cacheControl: 86400
    }).then(function() {
      return fs.read("/pkg/hey.js");
    }).then(function(blob) {
      assert.equal(blob.type, "text/javascript; charset=utf-8");
      return assert.equal(opt.cacheControl, 86400);
    });
  });
  it("should pass through calls to `clearCache`", function() {
    var called, fs, mock;
    fs = MountFS();
    called = false;
    mock = {
      clearCache: function() {
        return called = true;
      }
    };
    Bindable(null, mock);
    fs.mount("/", mock);
    fs.clearCache();
    return assert(called);
  });
  return it.skip("should list contents of mounted subfolders", function() {
    var fs, mock;
    fs = MountFS();
    mock = {
      list: function() {
        return Promise.resolve([
          {
            path: "a.txt"
          }, {
            path: "b.txt"
          }
        ]);
      }
    };
    Bindable(null, mock);
    fs.mount("/game/", mock);
    fs.mount("/", mock);
    return fs.list("/").then(function(results) {
      return assert.equal(results.length, 4);
    });
  });
});

