var Jadelet, Observable, dispatchEvent, makeTemplate, parser, sampleTemplate;

Observable = require("../observable");

Jadelet = require("../jadelet");

parser = require("../jadelet-parser");

sampleTemplate = "p\n  a(@click wat=@cool @butts)\n    @text\n    span Cool\n    @text2";

makeTemplate = function(src) {
  return Jadelet.exec(src);
};

dispatchEvent = function(element, eventName, options) {
  if (options == null) {
    options = {};
  }
  return element.dispatchEvent(new Event(eventName, options));
};


/*
AST is a tree of objects like:

    [tag, attributes, children]
 */

describe("Jadelet Runtime", function() {
  it("should render", function() {
    var ast, model, p;
    model = {
      cool: "radical",
      butts: function() {
        return "yolo";
      },
      text: Observable(["hollo world", " ", "yo", " ", "yo"]),
      text2: Observable(["yo wat"])
    };
    ast = parser.parse(sampleTemplate);
    p = Jadelet.exec(ast)(model);
    assert.equal(p.textContent, "hollo world yo yoCoolyo wat");
    model.text("yolo ");
    assert.equal(p.textContent, "yolo Coolyo wat");
    model.text2("rad");
    return assert.equal(p.textContent, "yolo Coolrad");
  });
  it("should compile strings", function() {
    var el, template;
    template = Jadelet.exec("h1 yolo");
    el = template();
    return assert.equal(el.textContent, "yolo");
  });
  return it("should return functions passed to exec", function() {
    var el, template;
    template = Jadelet.exec(Jadelet.exec("h1 yolo"));
    el = template();
    return assert.equal(el.textContent, "yolo");
  });
});

describe("Attributes", function() {
