describe("Events", function() {
  it("should bind click to the object context", function() {
    var button, model, result, template;
    template = makeTemplate("button(click=@click)");
    result = null;
    model = {
      name: Observable("Foobert"),
      click: function() {
        return result = this.name();
      }
    };
    button = template(model);
    assert.equal(result, null);
    button.click();
    return assert.equal(result, "Foobert");
  });
  it("should not error on non-functions when binding events", function() {
    var button, model, template;
    template = makeTemplate("button(@mouseenter @mouseleave)");
    model = {};
    button = template(model);
    dispatchEvent(button, "mouseenter");
    dispatchEvent(button, "mouseleave");
  });
  it("doesn't late bind event functions", function() {
    var button, called, model, template;
    template = makeTemplate("button(@click)");
    model = {};
    button = template(model);
    called = false;
    model.click = function() {
      return called = true;
    };
    dispatchEvent(button, "click");
    return assert(!called);
  });
  it("should bind mouseenter and mouseleave events", function() {
    var button, model, result, template;
    template = makeTemplate("button(@mouseenter @mouseleave)");
    result = null;
    model = {
      mouseenter: function() {
        return result = 1;
      },
      mouseleave: function() {
        return result = 2;
      }
    };
    button = template(model);
    assert.equal(result, null);
    dispatchEvent(button, "mouseenter");
    assert.equal(result, 1);
    dispatchEvent(button, "mouseleave");
    return assert.equal(result, 2);
  });
  it("shoud handle all touch events", function() {
    var called, canvas, eventFn, model, template;
    template = makeTemplate("canvas(@touchstart @touchmove @touchend @touchcancel)");
    called = 0;
    eventFn = function() {
      return called += 1;
    };
    model = {
      touchcancel: eventFn,
      touchstart: eventFn,
      touchmove: eventFn,
      touchend: eventFn
    };
    canvas = template(model);
    assert.equal(called, 0);
    dispatchEvent(canvas, "touchstart");
    assert.equal(called, 1);
    dispatchEvent(canvas, "touchmove");
    assert.equal(called, 2);
    dispatchEvent(canvas, "touchend");
    assert.equal(called, 3);
    dispatchEvent(canvas, "touchcancel");
    return assert.equal(called, 4);
  });
  return it("shoud handle all animation events", function() {
    var called, canvas, eventFn, model, template;
    template = makeTemplate("div(@animationstart @animationiteration @animationend @transitionend)");
    called = 0;
    eventFn = function() {
      return called += 1;
    };
    model = {
      animationstart: eventFn,
      animationend: eventFn,
      animationiteration: eventFn,
      transitionend: eventFn
    };
    canvas = template(model);
    assert.equal(called, 0);
    dispatchEvent(canvas, "animationstart");
    assert.equal(called, 1);
    dispatchEvent(canvas, "animationiteration");
    assert.equal(called, 2);
    dispatchEvent(canvas, "animationend");
    assert.equal(called, 3);
    dispatchEvent(canvas, "transitionend");
    return assert.equal(called, 4);
  });
});

