  model = {
    max: 10,
    value: Observable(5),
    options: function() {
      var _i, _ref, _results;
      return (function() {
        _results = [];
        for (var _i = 1, _ref = this.max; 1 <= _ref ? _i <= _ref : _i >= _ref; 1 <= _ref ? _i++ : _i--){ _results.push(_i); }
        return _results;
      }).apply(this).map(function(v) {
        var o;
        o = document.createElement("option");
        o.value = v;
        o.textContent = v;
