describe("content arrays", function() {
  it("should render and update items", function() {
    var element, model, template;
    template = makeTemplate("div\n  @items");
    model = {
      count: Observable(3),
      items: function() {
        var c, i, r;
        c = this.count();
        r = [];
        i = 0;
        while (i < c) {
          i++;
          r.push(document.createElement("p"));
        }
        return r;
      }
    };
    element = template(model);
    assert.equal(element.children.length, 3);
    model.count(5);
    return assert.equal(element.children.length, 5);
  });
  return it("should keep them in order and not re-render excessively", function() {
    var element, firstA, model, template;
    template = makeTemplate("div\n  @items\n  @otherItems\n  hr");
    model = {
      count: Observable(3),
      otherCount: Observable(2),
      items: function() {
        var c, i, r;
        c = this.count();
        r = [];
        i = 0;
        while (i < c) {
          i++;
          r.push(document.createElement("p"));
        }
        return r;
      },
      otherItems: function() {
        var c, i, r;
        c = this.otherCount();
        r = [];
        i = 0;
        while (i < c) {
          i++;
          r.push(document.createElement("a"));
        }
        return r;
      }
    };
    element = template(model);
    assert.equal(element.querySelectorAll('p').length, 3);
    assert.equal(element.querySelectorAll('a').length, 2);
    firstA = element.children[3];
    model.count(0);
    assert.equal(element.querySelectorAll('p').length, 0);
    assert.equal(element.querySelectorAll('a').length, 2);
    assert.equal(firstA, element.children[0]);
    model.count(7);
    assert.equal(element.querySelectorAll('p').length, 7);
    assert.equal(element.querySelectorAll('a').length, 2);
    assert.equal(firstA, element.children[7]);
    model.otherCount(1);
    return assert.notEqual(firstA, element.children[7]);
  });
});

