          {
            text: Observable("Wat")
          }, {
            text: Observable("is")
          }, {
            text: Observable("up")
          }
        ]);
        model = {
          rows: function() {
            return data.map(this.subtemplate);
          },
          subtemplate: makeTemplate("tr\n  td @text")
        };
        element = template(model);
        assert.equal(element.querySelectorAll("tr").length, 3);
        assert.equal(element.querySelector("td").textContent, "Wat");
        data()[0].text("yo");
        assert.equal(element.querySelector("td").textContent, "yo");
        data.push({
          text: Observable("dude")
        });
        assert.equal(element.querySelectorAll("tr").length, 4);
        assert.equal(element.querySelector("td").textContent, "yo");
        data()[0].text("holla");
        return assert.equal(element.querySelector("td").textContent, "holla");
      });
    });
  });
});

describe("element properties", function() {
  return it("should use the element property of objects rendered", function() {
    var element, src, template;
    src = "ul\n  @a\n  @b";
    template = makeTemplate(src);
    element = template({
      a: {
        element: document.createElement('a')
      },
      b: {
        element: document.createElement('b')
      }
    });
    assert(element.querySelector("a"));
    assert(element.querySelector("b"));
    return assert(!element.querySelector("c"));
  });
});

describe("cached retains", function() {
  return it("should not release elements that are consistent render to render", function() {
    var Container, Item, ItemView, cache, containerView, element, itemViews;
    Container = makeTemplate("div\n  @elements");
    Item = makeTemplate("input(@value)");
    ItemView = function(i) {
      var item, view;
      view = cache.get(i);
      if (view) {
        return view;
      }
      item = {
        value: Observable(i)
      };
      view = {
        item: item,
        element: Item(item)
      };
      cache.set(i, view);
      return view;
    };
    cache = new Map;
    itemViews = [0, 1, 2].map(ItemView);
    containerView = {
      elements: Observable(itemViews)
    };
    element = Container(containerView);
    assert.equal(element.children.length, 3);
    containerView.elements()[0].item.value(100);
    assert.equal(element.children[0].value, 100);
    containerView.elements.push(ItemView(3));
    assert.equal(element.children.length, 4);
    containerView.elements()[0].item.value(200);
    return assert.equal(element.children[0].value, 200);
  });
});

describe("text", function() {
