var Observable;

Observable = require("../observable");

describe('Observable', function() {
  it('should create an observable for an object', function() {
    var n, observable;
    n = 5;
    observable = Observable(n);
    return assert.equal(observable(), n);
  });
  it('should fire events when setting', function() {
    var observable, string;
    string = "yolo";
    observable = Observable(string);
    observable.observe(function(newValue) {
      return assert.equal(newValue, "4life");
    });
    return observable("4life");
  });
  it("should not fire when setting to the same value", function() {
    var o;
    o = Observable(5);
    o.observe(function() {
      return assert(false);
    });
    return o(5);
  });
  it('should be idempotent', function() {
    var o;
    o = Observable(5);
    return assert.equal(o, Observable(o));
  });
  it("should have releaseDependencies as a noop because primitive observables don't have any dependencies", function() {
    var o;
    o = Observable(5);
    return o.releaseDependencies();
  });
  it("should provide an updating non-observing semi-private reference to value", function() {
    var o, o2, o3;
    o = Observable(5);
    assert.equal(o._value, 5);
    o(7);
    assert.equal(o._value, 7);
    o2 = Observable(function() {
      return o._value;
    });
    o3 = Observable(function() {
      return o();
    });
    assert.equal(o2(), 7);
    assert.equal(o3(), 7);
    o(9);
    assert.equal(o2(), 7);
    assert.equal(o2._value, 7);
    assert.equal(o3(), 9);
    assert.equal(o3._value, 9);
    return assert.equal(o._value, 9);
  });
  it("should allow for stopping observation", function() {
    var called, fn, observable;
    observable = Observable("string");
    called = 0;
    fn = function(newValue) {
      called += 1;
      return assert.equal(newValue, "4life");
    };
    observable.observe(fn);
    observable("4life");
    observable.stopObserving(fn);
    observable("wat");
    return assert.equal(called, 1);
  });
  it("should do nothing when removing a listener that's not present", function() {
    var observable;
    observable = Observable("string");
    return observable.stopObserving(function() {});
  });
  it("should increment", function() {
    var observable;
    observable = Observable(1);
    observable.increment(5);
    assert.equal(observable(), 6);
    observable.increment();
    assert.equal(observable(), 7);
    observable.increment(0.05);
    assert.equal(observable(), 7.05);
    observable.increment(0.05);
    return assert.equal(observable(), 7.10);
  });
  it("should decremnet", function() {
    var observable;
    observable = Observable(1);
    observable.decrement(5);
    assert.equal(observable(), -4);
    observable.decrement();
    return assert.equal(observable(), -5);
  });
  it("should toggle", function() {
    var observable;
    observable = Observable(false);
    observable.toggle();
    assert.equal(observable(), true);
    observable.toggle();
    return assert.equal(observable(), false);
  });
  it("should trigger when toggling", function(done) {
    var observable;
    observable = Observable(true);
    observable.observe(function(v) {
      assert.equal(v, false);
      return done();
    });
    return observable.toggle();
  });
  return it("should have a nice toString", function() {
    var observable;
    observable = Observable(5);
    return assert.equal(observable.toString(), "Observable(5)");
  });
});

describe("Observable Array", function() {
  it("should proxy array methods", function() {
    var o;
    o = Observable([5]);
    return o.map(function(n) {
      return assert.equal(n, 5);
    });
  });
  it("should notify on mutation methods", function(done) {
    var o;
    o = Observable([]);
    o.observe(function(newValue) {
      return assert.equal(newValue[0], 1);
    });
    o.push(1);
    return done();
  });
  it("#get", function() {
    var o;
    o = Observable([0, 1, 2, 3]);
    return assert.equal(o.get(2), 2);
  });
  it("#first", function() {
    var o;
    o = Observable([0, 1, 2, 3]);
    return assert.equal(o.first(), 0);
  });
  it("#last", function() {
    var o;
    o = Observable([0, 1, 2, 3]);
    return assert.equal(o.last(), 3);
  });
  it("#remove", function() {
    var o;
    o = Observable([0, 1, 2, 3]);
    assert.equal(o.remove(2), 2);
    assert.equal(o.length, 3);
    assert.equal(o.remove(-5), void 0);
    return assert.equal(o.length, 3);
  });
  it("#remove non-existent element", function() {
    var o;
    o = Observable([1, 2, 3]);
    return assert.equal(o.remove(0), void 0);
  });
  it("should proxy the length property", function() {
    var called, o;
    o = Observable([1, 2, 3]);
    assert.equal(o.length, 3);
    called = false;
    o.observe(function(value) {
      assert.equal(value[0], 1);
      assert.equal(value[1], void 0);
      return called = true;
    });
    o.length = 1;
    assert.equal(o.length, 1);
    return assert.equal(called, true);
  });
  return it("should auto detect conditionals of length as a dependency", function() {
    var called, o, observableArray;
    observableArray = Observable([1, 2, 3]);
    o = Observable(function() {
      if (observableArray.length > 5) {
        return true;
      } else {
        return false;
      }
    });
    assert.equal(o(), false);
    called = 0;
    o.observe(function() {
      return called += 1;
    });
    observableArray.push(4, 5, 6);
    return assert.equal(called, 1);
  });
});

describe("Observable functions", function() {
  it("should compute dependencies", function(done) {
    var firstName, lastName, o;
    firstName = Observable("Duder");
    lastName = Observable("Man");
    o = Observable(function() {
      return "" + (firstName()) + " " + (lastName());
    });
    o.observe(function(newValue) {
      assert.equal(newValue, "Duder Bro");
      return done();
    });
    return lastName("Bro");
  });
  it("should compute array#get as a dependency", function() {
    var observableArray, observableFn;
    observableArray = Observable([0, 1, 2]);
    observableFn = Observable(function() {
      return observableArray.get(0);
    });
    assert.equal(observableFn(), 0);
    observableArray([5]);
    return assert.equal(observableFn(), 5);
  });
  it("should compute array#first as a dependency", function() {
    var observableArray, observableFn;
    observableArray = Observable([0, 1, 2]);
    observableFn = Observable(function() {
      return observableArray.first() + 1;
    });
    assert.equal(observableFn(), 1);
    observableArray([5]);
    return assert.equal(observableFn(), 6);
  });
  it("should compute array#last as a dependency", function() {
    var observableArray, observableFn;
    observableArray = Observable([0, 1, 2]);
    observableFn = Observable(function() {
      return observableArray.last();
    });
    assert.equal(observableFn(), 2);
    observableArray.pop();
    assert.equal(observableFn(), 1);
    observableArray([5]);
    return assert.equal(observableFn(), 5);
  });
  it("should compute array#size as a dependency", function() {
    var observableArray, observableFn;
    observableArray = Observable([0, 1, 2]);
    observableFn = Observable(function() {
      return observableArray.size() * 2;
    });
    assert.equal(observableFn(), 6);
    observableArray.pop();
    assert.equal(observableFn(), 4);
    observableArray.shift();
    return assert.equal(observableFn(), 2);
  });
  it("should allow double nesting", function(done) {
    var bottom, middle, top;
    bottom = Observable("rad");
    middle = Observable(function() {
      return bottom();
    });
    top = Observable(function() {
      return middle();
    });
    top.observe(function(newValue) {
      assert.equal(newValue, "wat");
      assert.equal(top(), newValue);
      assert.equal(middle(), newValue);
      return done();
    });
    return bottom("wat");
  });
  it("should work with dynamic dependencies", function() {
    var dynamicObservable, observableArray;
    observableArray = Observable([]);
    dynamicObservable = Observable(function() {
      return observableArray.filter(function(item) {
        return item.age() > 3;
      });
    });
    assert.equal(dynamicObservable().length, 0);
    observableArray.push({
      age: Observable(1)
    });
    observableArray()[0].age(5);
    return assert.equal(dynamicObservable().length, 1);
  });
  it("should work with context", function() {
    var model;
    model = {
      a: Observable("Hello"),
      b: Observable("there")
    };
    model.c = Observable(function() {
      return "" + (this.a()) + " " + (this.b());
    }, model);
    assert.equal(model.c(), "Hello there");
    model.b("world");
    return assert.equal(model.c(), "Hello world");
  });
  it("should be ok even if the function throws an exception", function() {
    assert.throws(function() {
      var t;
      return t = Observable(function() {
        throw "wat";
      });
    });
    return assert.equal(global.OBSERVABLE_ROOT_HACK.length, 0);
  });
  it("should work on an array dependency", function() {
    var last, o, oA;
    oA = Observable([1, 2, 3]);
    o = Observable(function() {
      return oA()[0];
    });
    last = Observable(function() {
      return oA()[oA().length - 1];
    });
    assert.equal(o(), 1);
    oA.unshift(0);
    assert.equal(o(), 0);
    oA.push(4);
    return assert.equal(last(), 4, "Last should be 4");
  });
  it("should work with multiple dependencies", function() {
    var checked, first, letter, second;
    letter = Observable("A");
    checked = function() {
      var l;
      l = letter();
      return this.name().indexOf(l) === 0;
    };
    first = {
      name: Observable("Andrew")
    };
    first.checked = Observable(checked, first);
    second = {
      name: Observable("Benjamin")
    };
    second.checked = Observable(checked, second);
    assert.equal(first.checked(), true);
    assert.equal(second.checked(), false);
    assert.equal(letter.listeners.length, 2);
    letter("B");
    assert.equal(first.checked(), false);
    return assert.equal(second.checked(), true);
  });
  it("shouldn't double count dependencies", function() {
    var count, dep, o;
    dep = Observable("yo");
    o = Observable(function() {
      dep();
      dep();
      return dep();
    });
    count = 0;
    o.observe(function() {
      return count += 1;
    });
    dep('heyy');
    return assert.equal(count, 1);
  });
  it("should recompute the correct number of times", function() {
    var called, fn, items, joiner;
    joiner = Observable(",");
    items = Observable(["A", "B", "C"]);
    called = 0;
    fn = Observable(function() {
      called += 1;
      return items.join(joiner());
    });
    assert.equal(fn(), "A,B,C");
    assert.equal(called, 1);
    items.push("D");
    assert.equal(fn(), "A,B,C,D");
    assert.equal(called, 2);
    joiner(".");
    assert.equal(fn(), "A.B.C.D");
    assert.equal(called, 3);
    items.push("E");
    assert.equal(fn(), "A.B.C.D.E");
    return assert.equal(called, 4);
  });
  it("should work with nested observable construction", function() {
    var gen, o, o2;
    gen = Observable(function() {
      return Observable("Duder");
    });
    o = gen();
    o2 = gen();
    assert.equal(o, o2);
    assert.equal(o(), "Duder");
    o("wat");
    return assert.equal(o(), "wat");
  });
  return it("should be scoped to optional context", function(done) {
    var model;
    model = {
      firstName: Observable("Duder"),
      lastName: Observable("Man")
    };
    model.name = Observable(function() {
      return "" + (this.firstName()) + " " + (this.lastName());
    }, model);
    model.name.observe(function(newValue) {
      assert.equal(newValue, "Duder Bro");
      return done();
    });
    return model.lastName("Bro");
  });
});

