var ModLoader, Require, compile, crudeRequire, exec, htmlForPackage, jsForPackage, minify, registerCompiler, testPkg, _ref;

_ref = require("/lib/pkg/index"), compile = _ref.compile, crudeRequire = _ref.crudeRequire, exec = _ref.exec, htmlForPackage = _ref.htmlForPackage, jsForPackage = _ref.jsForPackage, minify = _ref.minify, ModLoader = _ref.ModLoader, registerCompiler = _ref.registerCompiler, Require = _ref.Require;

mocha.setup({
  globals: ['CoffeeScript', 'marked', 'stylus', 'UglifyJS']
});

testPkg = {
  distribution: {
    main: {
      content: "alert('heyy');"
    }
  },
  dependencies: {
    "!system": PACKAGE
  },
  config: {
    description: "Yolo"
  }
};

describe("pkg", function() {
  describe("htmlForPackage", function() {
    return it("should blob up html", function() {
      var blob;
      blob = htmlForPackage(testPkg);
      return assert(blob instanceof Blob);
    });
  });
  describe("compile", function() {
    it("should compile CoffeeScript by lazy loading compiler", function() {
      return compile("hello.coffee", "alert 'hello'");
    });
    it("should 'compile' JavaScript", function() {
      var src;
      src = "alert('hello');";
      return compile("hello.js", src).then(function(program) {
        return assert.equal(program, src);
      });
    });
    it("should compile markdown after lazy loading compiler", function() {
      return compile("TODO.md", "- [x] Lazy Load compilers");
    });
    it("should compile stylus after lazy loading compiler", function() {
      return compile("yo.styl", "body\n  background-color: green");
    });
    it("should fail if no known compiler", function() {
      return compile("wat.doot", "")["catch"](function(e) {
        return assert.equal(e.message, "Couldn't compile 'wat.doot'. No compiler for '.doot'");
      });
    });
    return it("should register compilers", function() {
      registerCompiler("doot", function() {
        return "doot";
      });
      return compile("wat.doot", "");
    });
  });
  describe("exec", function() {
    it("should bind this", function() {
      var x, y;
      x = {};
      y = exec("return this", null, x);
      return assert.equal(x, y);
    });
    it("env is optional", function() {
      var r;
      r = exec("return 5");
      return assert.equal(r, 5);
    });
    return it("should bind env values", function() {
      var r;
      r = exec("return a + this", {
        a: 2
      }, 3);
      return assert.equal(r, 5);
    });
  });
  describe("crudeRequire", function() {
    return it("should return what is exported", function() {
      var result;
      result = crudeRequire("module.exports = \"cool\";");
      return assert.equal(result, "cool");
    });
  });
  describe("Require", function() {
    return it("should be exported", function() {
      return assert(Require);
    });
  });
  describe("jsForPackge", function() {
    return it("should generate a string of js", function() {
      var js;
      js = jsForPackage(PACKAGE);
      return assert(typeof js === 'string');
    });
  });
  describe.skip("minify", function() {
    return it("makes packages smaller", function() {
      return minify(PACKAGE, console).then(console.log);
    });
  });
  return describe("Mod Loader", function() {
    return it("should load remote modules", function() {
      return ModLoader;
    });
  });
});

