var latestRequire, requireSrc, sampleDir;

requireSrc = "/lib/pkg/require2";

latestRequire = require(requireSrc).generateFor(PACKAGE);

sampleDir = "/data/pkg/samples/";

describe("require", function() {
  it("should not exist globally", function() {
    return assert(!global.require);
  });
  it("should be able to require a file that exists with a relative path", function() {
    return assert(latestRequire("" + sampleDir + "terminal"));
  });
  it("should get whatever the file exports", function() {
    return assert(latestRequire("" + sampleDir + "terminal").something);
  });
  it("should not get something the file doesn't export", function() {
    return assert(!latestRequire("" + sampleDir + "terminal").something2);
  });
  it("should throw a descriptive error when requring circular dependencies", function() {
    return assert.throws(function() {
      return latestRequire("" + sampleDir + "circular");
    }, /circular/i);
  });
  it("should throw a descriptive error when requiring a package that doesn't exist", function() {
    return assert.throws(function() {
      return latestRequire("does_not_exist");
    }, /not found/i);
  });
  it("should throw a descriptive error when requiring a relative path that doesn't exist", function() {
    return assert.throws(function() {
      return latestRequire("/does_not_exist");
    }, /Could not find file/i);
  });
  it("should recover gracefully enough from requiring files that throw errors", function() {
    assert.throws(function() {
      return latestRequire("" + sampleDir + "throws");
    });
    return assert.throws(function() {
      return latestRequire("" + sampleDir + "throws");
    }, function(err) {
      return !/circular/i.test(err);
    });
  });
  it("should cache modules", function() {
    var result;
    result = latestRequire("" + sampleDir + "random");
    return assert.equal(latestRequire("" + sampleDir + "random"), result);
  });
  it("should be able to require a JSON package object", function() {
    var SAMPLE_PACKAGE, result;
    SAMPLE_PACKAGE = {
      entryPoint: "main",
      distribution: {
        main: {
          content: "module.exports = require('./other')"
        },
        other: {
          content: "module.exports = 'TEST'"
        }
      }
    };
    result = latestRequire(SAMPLE_PACKAGE);
    return assert.equal("TEST", result);
  });
  return it("should be able to require something packaged with browserify", function() {
    return assert.equal(latestRequire("" + sampleDir + "browserified"), "coolio");
  });
});

describe("package wrapper", function() {
  it("should be able to generate a package wrapper recursively", function() {
    var pkgString;
    pkgString = latestRequire.packageWrapper(PACKAGE, "window.r = require");
    Function(pkgString)();
    Function(r.packageWrapper(PACKAGE, "window.r2 = require"))();
    Function(r2.packageWrapper(PACKAGE, "window.r3 = require"))();
    assert(r2);
    assert(r3);
    delete r;
    delete r2;
    return delete r3;
  });
  return it("should be able to execute code in the package context", function() {
    var code;
    code = latestRequire.packageWrapper(PACKAGE, "window.test = require.packageWrapper(PACKAGE, 'alert(\"heyy\")');");
    Function(code)();
    assert(window.test);
    return delete window.test;
  });
});

describe("public API", function() {
  mocha.setup({
    globals: ['system', 'OBSERVABLE_ROOT_HACK']
  });
  return it("should be able to require a JSON package directly", function() {
    assert(require(requireSrc).loadPackage({
      distribution: {
        main: {
          content: "global.test2 = true"
        }
      }
    }));
    assert(window.test2);
    return delete window.test2;
  });
});

describe("module context", function() {
  it("should know __dirname", function() {
    return assert.equal("lib/test/pkg", __dirname);
  });
  it("should know __filename", function() {
    return assert(__filename);
  });
  return it("should know its package", function() {
    return assert(PACKAGE);
  });
});

describe("malformed package", function() {
  var malformedPackage;
  malformedPackage = {
    distribution: {
      yolo: "No content!"
    }
  };
  return it("should throw an error when attempting to require a malformed file in a package distribution", function() {
    var r;
    r = require(requireSrc).generateFor(malformedPackage);
    return assert.throws(function() {
      return r.require("yolo");
    }, function(err) {
      return !/malformed/i.test(err);
    });
  });
});

describe("dependent packages", function() {
  it("should allow for arbitrary characters", function() {
    var r;
    r = require(requireSrc).generateFor({
      dependencies: {
        "#$!jadelet": {
          entryPoint: "main",
          distribution: {
            main: {
              content: "module.exports = 'ok';"
            }
          }
        }
      }
    });
    return assert.equal(r("#$!jadelet"), "ok");
  });
  PACKAGE.dependencies["test-package"] = {
    distribution: {
