:root {
  --neutral-light: rgba(255,255,255,0.5);
  --neutral-faintest: #efefef;
  --neutral-faint: rgba(0,0,0,0.25);
  --neutral-dark: rgba(0,0,0,0.5);
  --neutral-darker: rgba(0,0,0,0.875);
  --shadow-low: 1px 2px 0;
  --shadow-medium: 4px 4px 0;
  --border-radius: 0;
  --background-color: #fff;
  --background-neutral: #dcdcdc;
  --dark-subdued-color: #000080;
  --error-color: #800;
  --highlight-color: #ffb845;
  --hover-color: #d2d9ef;
  --primary-color: #8549ad;
  --text-color: rgba(0,0,0,0.938);
  --text-primary-color: #fff;
}
* {
  box-sizing: border-box;
}
html {
  color: var(--text-color);
  font-family: 'Fira Sans', Sans-Serif;
  font-size: 16px;
  line-height: 1.5;
}
body,
html {
  height: 100%;
}
body {
  display: flex;
  flex-direction: column;
  margin: 0;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  margin: 0;
}
iframe {
  border: none;
}
p {
  margin: 0 0 1rem;
}
ul {
  list-style: none;
  margin: 0;
  padding: 0;
}
input,
textarea,
keygen,
select,
button {
  color: inherit;
  font: inherit;
  line-height: inherit;
}
:focus-visible {
  outline: 2px solid var(--text-color);
}
::-webkit-scrollbar {
  border: 1px solid var(--neutral-darker);
  background-color: var(--background-neutral);
  border-radius: var(--border-radius);
}
::-webkit-scrollbar-thumb {
  background-color: var(--background-color);
  border: 1px solid var(--neutral-darker);
  border-radius: var(--border-radius);
}
::-webkit-scrollbar-thumb:hover {
  background-color: var(--neutral-faintest);
}
button,
a.button,
input[type="file"]::file-selector-button {
  background-color: var(--background-color);
  border: 1px solid var(--neutral-darker);
  border-radius: var(--border-radius);
  box-shadow: var(--shadow-low) var(--neutral-dark);
  cursor: pointer;
  display: inline-block;
  padding: 0.125em 0.375em;
}
button:hover,
a.button:hover {
  background-color: var(--neutral-faintest);
}
button:active,
a.button:active,
button.active,
a.button.active {
  background-color: var(--neutral-faintest);
  box-shadow: var(--shadow-low) var(--neutral-dark) inset;
}
input[type="file"]:hover::file-selector-button {
  background-color: var(--neutral-faintest);
}
input[type="file"]:active::file-selector-button,
input[type="file"].active::file-selector-button {
  background-color: var(--neutral-faintest);
  box-shadow: var(--shadow-low) var(--neutral-dark) inset;
}
input,
textarea {
  border: 1px solid var(--neutral-darker);
  border-radius: var(--border-radius);
  box-shadow: var(--shadow-low) var(--neutral-dark) inset;
  padding: 0.125em 0.25em;
}
input::placeholder,
textarea::placeholder {
  font-style: italic;
}
input[type="file"] {
  border-width: 0;
  box-shadow: none;
  padding: 3px 0;
}
label > input,
label > textarea,
label > select {
  width: 100%;
}
loader {
  display: block;
  padding: 1em;
}
loader > p:empty {
  margin: 0;
}
loader > progress {
  display: block;
}
menu {
  user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  background-color: var(--background-neutral);
  border-bottom: 1px solid var(--neutral-dark);
  line-height: 1.25;
  margin: 0;
  z-index: 1;
}
menu:focus {
  outline: none;
}
menu.context {
  z-index: 2000;
}
menu-item {
  display: block;
  list-style-type: none;
}
menu-item.active {
  background-color: var(--dark-subdued-color);
  color: var(--text-primary-color);
}
menu-item > label {
  display: flex;
  padding: 0 0.25rem;
  white-space: nowrap;
}
menu-item > label > * {
  flex: 1 1 auto;
}
menu-item > label > span.hotkey {
  margin-left: 1rem;
}
menu-item > label > span.hotkey:empty {
  margin-left: 0;
}
menu-item > label > .decoration {
  align-self: center;
  flex: 0 1 auto;
  text-align: right;
  margin-left: 0.5rem;
}
menu-item > label > .decoration:empty {
  margin-left: 0;
}
menu-item[disabled] {
  color: #808080;
}
menu-item[disabled].active {
  background-color: rgba(0,0,0,0.125);
}
menu.options {
  border-top: 1px solid var(--neutral-light);
  border-left: 1px solid var(--neutral-light);
  border-right: 1px solid var(--neutral-dark);
  box-shadow: var(--shadow-low) var(--neutral-dark);
  display: none;
  padding: 2px;
  padding-bottom: 3px;
  position: absolute;
}
menu.options.active {
  display: block;
}
menu.options > menu-item.menu {
  position: relative;
}
menu.options > menu-item.menu > menu {
  position: absolute;
  left: 100%;
  top: -3px;
  margin-left: 1px;
}
menu-item.menu.active > menu {
  background-color: var(--background-neutral);
  color: var(--text-color);
  display: block;
}
menu.bar {
  display: block;
  flex: 0 0 auto;
  margin: 0;
  padding: 0;
  position: initial;
  white-space: nowrap;
  overflow: hidden;
}
menu.bar > menu-item {
  display: inline-block;
}
menu.bar > menu-item > label > .decoration {
  display: none;
}
menu.bar.accelerator-active span.accelerator {
  text-decoration: underline;
}
menu.options.bottoms-up menu-item.menu > menu {
  top: initial;
  bottom: -4px;
}
#modal {
  align-items: center;
  background-color: var(--neutral-faint);
  display: none;
  justify-content: center;
  position: absolute;
  z-index: 10000;
  top: 0;
  width: 100%;
  height: 100%;
}
#modal.active {
  display: flex;
}
#modal > * {
  background-color: var(--background-color);
  border: 1px solid var(--neutral-dark);
  border-radius: var(--border-radius);
  box-shadow: var(--shadow-medium) var(--neutral-dark);
  max-width: 90%;
  max-height: 90%;
  position: relative;
  z-index: 1;
}
#modal > form {
  display: block;
  padding: 1rem;
}
#modal > form > h1,
#modal > form > h2 {
  font-size: 1.5rem;
  line-height: 1.5rem;
  margin-bottom: 0.5rem;
}
#modal > form > input,
#modal > form > textarea {
  display: block;
  margin-bottom: 1rem;
  width: 100%;
}
#modal > form > button {
  margin-right: 1rem;
}
#modal > form > button:last-child {
  margin-right: 0;
}
#modal > section.ws-login {
  padding: 2rem;
  position: relative;
}
#modal > section.ws-login > form {
  margin-right: 0;
  width: 400px;
}
#modal > section.ws-login > form > h1 {
  margin-top: 0;
}
#modal > section.ws-login > form > section > button.top-left {
  position: absolute;
  top: 0.5rem;
  left: 0.5rem;
}
#modal > form.fuzzy-list {
  display: flex;
  flex-direction: column;
  width: 66.6%;
  height: 66.6%;
}
form.fuzzy-list > input {
  width: 100%;
}
form.fuzzy-list > ul {
  flex: 1 1 auto;
  overflow: auto;
}
form.fuzzy-list > ul > li {
  cursor: pointer;
}
form.fuzzy-list > ul > li:nth-child(even) {
  background-color: var(--neutral-faintest);
}
form.fuzzy-list > ul > li:hover {
  background-color: var(--hover-color);
}
form.fuzzy-list > ul > li.active {
  background-color: var(--highlight-color);
}
@media only screen and (max-width: 768px) {
  #modal > form.fuzzy-list {
    width: 90%;
    height: 90%;
  }
}
container {
  display: block;
  height: 100%;
  overflow: auto;
  width: 100%;
}
table {
  border-collapse: collapse;
  width: 100%;
}
th {
  text-align: left;
}
thead {
  border-bottom: 1px solid var(--neutral-darker);
}
td > input {
  border: none;
  box-shadow: none;
  background-color: transparent;
  padding: 0;
  width: 100%;
}
tr:nth-child(even) {
  background-color: var(--neutral-faintest);
}
window {
  user-select: none;
  -webkit-user-select: none;
  -ms-user-select: none;
  background-color: var(--background-neutral);
  border: 4px double var(--neutral-darker);
  border-radius: var(--border-radius);
  box-shadow: var(--shadow-low) var(--neutral-dark);
  display: flex;
  flex-direction: column;
  position: absolute;
}
window > header {
  background-color: var(--dark-subdued-color);
  border-bottom: 1px solid var(--neutral-darker);
  cursor: default;
  display: flex;
  flex: 0 0 auto;
  line-height: 18px;
}
window > header > icon {
  background-position: 50%;
  background-repeat: no-repeat;
  background-size: 16px;
  color: var(--text-primary-color);
  display: inline-block;
  flex: 0 0 auto;
  text-align: center;
  width: 0;
}
window > header > control {
  align-items: center;
  background-color: var(--background-neutral);
  border-left: 1px solid var(--neutral-darker);
  color: var(--text-primary-color);
  cursor: pointer;
  display: inline-flex;
  flex: 0 0 auto;
  font-family: 'Fira Code', monospace;
  justify-content: center;
  overflow: hidden;
  text-shadow: 1px 0px 0px #000, -1px 0px 0px #000, 0px -1px 0px #000, 0px 1px 0px #000, 1px 1px 0px #808080;
  width: 18px;
}
window > header > control:first-child {
  border-left: none;
}
window > header > control.close::before {
  content: "X";
}
window > header > control.maximize::before {
  content: "+";
}
window > header > control.minimize::before {
  content: "-";
}
window > header > control.restore {
  display: none;
}
window > header > control.restore::before {
  content: "-";
}
window > header > title-bar {
  color: var(--text-primary-color);
  display: inline-block;
  flex: 1 1 auto;
  overflow: hidden;
  padding: 0 1rem 0 6px;
  text-overflow: ellipsis;
  white-space: nowrap;
}
window > viewport {
  background-color: #fff;
  display: flex;
  height: 100%;
  overflow: auto;
  position: relative;
  z-index: 0;
}
window > viewport > * {
  margin: auto;
}
window > viewport > textarea {
  border: none;
  font-family: monospace;
  height: 100%;
  padding: 2px 4px;
  resize: none;
  width: 100%;
}
window > viewport > section.ace_editor {
  width: 100%;
  height: 100%;
}
window > viewport > iframe {
  border: none;
  height: 100%;
  width: 100%;
  position: absolute;
}
window > resize {
  border-color: var(--neutral-darker);
  display: block;
  position: absolute;
}
window > resize.e,
window > resize.w {
  cursor: ew-resize;
}
window > resize.n,
window > resize.s {
  cursor: ns-resize;
}
window > resize.h {
  height: 4px;
  width: 100%;
}
window > resize.v {
  height: 100%;
  width: 4px;
}
window > resize.w {
  left: -4px;
}
window > resize.e {
  right: -4px;
}
window > resize.n {
  top: -4px;
}
window > resize.s {
  bottom: -4px;
}
window > resize.n.w {
  cursor: nw-resize;
}
window > resize.n.e {
  cursor: ne-resize;
}
window > resize.s.e {
  cursor: se-resize;
}
window > resize.s.w {
  cursor: sw-resize;
}
window > resize.n.v {
  border-bottom: 1px solid;
  height: 23px;
}
window > resize.s.v {
  border-top: 1px solid;
  height: 23px;
}
window > resize.e.h {
  border-left: 1px solid;
  width: 22px;
}
window > resize.w.h {
  border-right: 1px solid;
  width: 22px;
}
window.minimized > header {
  border-bottom: none;
}
window.minimized > header > control {
  display: none;
}
window.minimized > header > control.minimize {
  border-right: none;
  display: inline-flex;
}
window.minimized > header > control.minimize::before {
  content: "+";
}
window.minimized > menu,
window.minimized > resize,
window.minimized > viewport {
  display: none;
}
window.maximized {
  border: none;
  border-radius: 0;
  height: 100%;
  width: 100%;
}
window.maximized > resize {
  display: none;
}
window.maximized > header {
  position: absolute;
  right: 0;
  z-index: 2;
}
window.maximized > header > control {
  display: none;
}
window.maximized > header > control.restore,
window.maximized > header > control.close {
  display: inline-flex;
}
frame-guard {
  display: block;
  height: 100%;
  left: 0;
  pointer-events: none;
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 100000;
}
frame-guard.active {
  pointer-events: auto;
}
