module.exports = function(options) {
  var input;
  if (options == null) {
    options = {};
  }
  input = document.createElement('input');
  input.type = "file";
  input.setAttribute("accept", options.accept);
  input.onchange = function(e) {
    return typeof options.select === "function" ? options.select(input.files[0]) : void 0;
  };
  return input;
};

test/menu-item.content:
var MenuItemView, Observable;

PACKAGE.name = "test";

require("../main");

MenuItemView = require("../views/menu-item");

Observable = require("../lib/observable");

describe("MenuItem", function() {
  return it("should have correct custom action names", function() {
    var called, menuItem;
    called = false;
    menuItem = MenuItemView({
      label: "Cool -> Super Cool",
      contextRoot: {
        activeItem: function() {},
        handlers: {
          "Super Cool": function() {
            return called = true;
          }
        }
      }
    });
    assert(!called);
    menuItem.click();
    return assert(called);
  });
});

test/menu-parser.content:
var parse;

parse = require("../lib/indent-parse");

describe("Menu Parser", function() {
  it("should parse menus into lists", function() {
    var data, results;
    data = "File";
    results = parse(data);
    return assert.deepEqual(["File"], results);
  });
  it("should parse empty", function() {
    var data;
    data = "";
    return assert.deepEqual([], parse(data));
  });
  it("should deal with nesting ok", function() {
    var data, results;
    data = "File
  Open
  Save
Edit
  Copy
  Paste
Help";
    results = parse(data);
    return assert.deepEqual([["File", ["Open", "Save"]], ["Edit", ["Copy", "Paste"]], "Help"], results);
  });
  it("should parse big ol' menus", function() {
    var results;
    results = parse("File
  New
  Open
  Save
  Save As
Edit
  Undo
  Redo
  -
  Cut
  Copy
  Paste
  Delete
  -
  Find
  Find Next
  Replace
  Go To
  -
  Select All
  Time/Date
Format
  Word Wrap
  Font...
View
  Status Bar
Help
  View Help
  -
  About Notepad");
    return assert.deepEqual([["File", ["New", "Open", "Save", "Save As"]], ["Edit", ["Undo", "Redo", "-", "Cut", "Copy", "Paste", "Delete", "-", "Find", "Find Next", "Replace", "Go To", "-", "Select All", "Time/Date"]], ["Format", ["Word Wrap", "Font..."]], ["View", ["Status Bar"]], ["Help", ["View Help", "-", "About Notepad"]]], results);
  });
  return it("should parse hella nested menus", function() {
    var results;
    results = parse("File
  Special
    Nested
      Super
        Awesome");
    return assert.deepEqual([["File", [["Special", [["Nested", [["Super", ["Awesome"]]]]]]]]], results);
  });
});

test/menu.content:
var MenuView, Observable;

MenuView = require("../views/menu");

Observable = require("../lib/observable");

describe("Menu", function() {
  it("should work with plain ol' items", function() {
    var menu;
    menu = MenuView({
      items: ["Cool", "Rad"],
      contextRoot: {
        activeItem: Observable(null),
        handlers: {}
      }
    });
    return assert.equal(menu.items().length, 2);
  });
  return it("should allow observable items", function() {
    var items, menu;
    items = Observable(["Cool", ["Rad", ["2rad", "2Furious"]]]);
    menu = MenuView({
      items: items,
      contextRoot: {
        activeItem: Observable(null),
        handlers: {}
      }
    });
    assert.equal(menu.items().length, 2);
    items(["New Stuff"]);
    return assert.equal(menu.items().length, 1);
  });
});

test/modal.content:
